drop table if exists CUSTOMER_DETAILS;
CREATE TABLE CUSTOMER_DETAILS(
CUST_ID INT NOT NULL,
TITLE	VARCHAR(5),
FIRST_NAME	VARCHAR(20),
LAST_NAME	VARCHAR(100),
DOB	VARCHAR(100),
GENDER	VARCHAR(1),
MARITAL_STATUS	VARCHAR(10),
NATIONALITY	VARCHAR(50),
ADDRESS_1	VARCHAR(100),
ADDRESS_2	VARCHAR(100),
ADDRESS_3	VARCHAR(100),
CITY	VARCHAR(20),
STATE	VARCHAR(20),
COUNTRY	VARCHAR(20),
PINCODE	INT,
HOME_NUMBER	INT,
PHONE_NUMBER	INT,
EMAIL_ID	VARCHAR(50)
);

drop table if exists FAMILY_DETAILS;
CREATE TABLE FAMILY_DETAILS(
CUST_ID INT NOT NULL,
S_ID INT NOT NULL AUTO_INCREMENT,	
S_FIRST_NAME	VARCHAR(20),
S_LAST_NAME	VARCHAR(20),
S_DOB	VARCHAR(15),
S_GENDER	VARCHAR(1),
S_NATIONALITY	VARCHAR(50),
S_MOBILE_NUMBER	INT,
S_EMAIL_ID	VARCHAR(50)
);

drop table if exists EMERGENCY_DETAILS;
CREATE TABLE EMERGENCY_DETAILS(	
CUST_ID INT NOT NULL,
E_ID INT NOT NULL AUTO_INCREMENT,
E_FIRST_NAME	VARCHAR(20),
E_LAST_NAME	VARCHAR(20),
E_MOBILE_NUMBER	INT,
E_RELATIONSHIP	VARCHAR(20)
);



